import React, { useEffect, useState } from "react";
import BotsCollection from "./BotCollection";

function Display() {
  let [bot, setBots] = useState([])

  useEffect(() => {
    fetch("http://localhost:3000/bots")
      .then((resp) => resp.json())
      .then(bot => setBots(bot))
  }, [])

  const enlistBot= (bot)=>{
    setBots(bot.map((b)=>(b.id === bot.id ? {...b, army:true} : b)))
}
const removeBot =(bot)=>{
    setBots(bot.map((b)=>(b.id === bot.id ? {...b, army:false} : b)))
}

const deleteBot = (bot)=>{
    const botRemoved =  bot.filter((b)=> b.id !==bot.id);
    setBots((bots)=>botRemoved)
}
  return (
    <div>
    <BotListArmy
            bots={bot.filter((b)=>b.army)}
            removeBot={removeBot}
            deleteBot={deleteBot}

            />
 <BotsCollection bots = {bot}/>
 </div>
  )
}

export default Display;
